//created using rafce (set-up)
import PropTypes from 'prop-types'
import Button from './Button'

//this sets title = 1st property of the object (props) we receive 
const Header = ( {title} ) => { // receive the property 'title' from the object (props)
    const onClick = () => {
        console.log('Click')
    }

    return (
        <header className='header'>
            <h1>{title}</h1>
            {/* pass the onClick function as a prop */}
            <Button color={'green'} text={'Add'} onClick = {onClick}/>
        </header>
    )
}

//if <Header /> is passed with smth, this is overwritten
Header.defaultProps = {
    title: 'Task Tracker',
}

Header.propTypes = {
    title: PropTypes.string.isRequired,
}

// const headingStyle = {
//     color: 'red', backgroundColor: 'black'
// }

export default Header
