//TODO:
//let users choose color with scroller instead of hardcoding it

import { useState, createContext, useContext} from 'react'
import Select from 'react-select'


const Lobby = () => {

    const [players, setPlayers] = useState([
        {
            id: 1,
            color: 'red'
        },
        {
            id: 2,
            color: 'blue'
        },
        {
            id: 3,
            color: 'green'
        },
        {
            id: 4,
            color: 'purple'
        }
    ])

    const [selects, setSelects] = useState();

    const changeColor = (player, toColor) => {
        const id = Math.floor(Math.random() * 10000) + 1
        let dummy = { ...player }
        dummy.color = toColor
        setPlayers([...players, dummy])
    }

    const Players = ({players}) => {
        return (
            <>
                {players.map((player) => (
                    <Player key={player.id} player={player}/> 

                ))}
            </>
        )
    }

    const Player = ({player}) => { //onDelete is a function passed up (to the parent file)
        //onDelete = passed up (executed in parent func) while task = passed down (executed here)
        const colors = [
            { value: 'red', label: 'red'},
            { value: 'blue', label: 'blue'},
            { value: 'green', label: 'green'},
            { value: 'purple', label: 'purple'}
          ]

        return (
            <div className='container' style={{backgroundColor: player.color}}>
                <Select options={colors} onChange={e=>console.log(e.target.value)}/>
            </div>
      )
    }
    return (
        <div>
            <Players players={players} />
        </div>
    )
}

export default Lobby
