//created using rafce (set-up)
import PropTypes from 'prop-types'
import Button from './Button'

//this sets title = 1st property of the object (props) we receive 
const Header = ( {title, onAdd, showAdd} ) => { // receive the property 'title' from the object (props)

    return (
        <header className='header'>
            <h1>{title}</h1>
            {/* pass the onClick function as a prop */}
            <Button 
                color={showAdd ? 'red' : 'green'} 
                text={showAdd ? 'Close' : 'Add'} 
                onAdd = {onAdd}
            />
        </header>
    )
}

//if <Header /> is passed with smth, this is overwritten
Header.defaultProps = {
    title: 'Task Tracker',
}

Header.propTypes = {
    title: PropTypes.string.isRequired,
}

// const headingStyle = {
//     color: 'red', backgroundColor: 'black'
// }

export default Header
