import { BrowserRouter, Route, Routes} from 'react-router-dom'
import { useContext } from 'react'
import Lobby from './components/Lobby';
import {AppContext, AppContextProvider} from './components/Context';
import React from 'react';

const App = () => {
  // All hooks are defined in App.js (highest component & Provided as context in the return statement)
  const { players, colors, setPlayers, setColors } = useContext(AppContext)



  return (
    // NOTE: this context is also provided for its grandchildren (X need several Providers)
      <AppContextProvider>
        <BrowserRouter>
          <div >
              <Routes>
                  <Route path="/" element={<Lobby/>}/>
                {/* <Route path="/about" element={<ro/>}/> */}
              </Routes>
          </div> 
        </BrowserRouter>
      </AppContextProvider>
  );
}

// export to index.js
export default App;
