import { FaTimes } from 'react-icons/fa'

const Task = ({task, onDelete}) => { //onDelete is a function passed up (to the parent file)
    //onDelete = passed up (executed in parent func) while task = passed down (executed here)
    return (
        <div className="task">
            <h3>
                {task.text} <FaTimes style={{ color: 'red', cursor: 'pointer' }}
                onClick={onDelete}/>
                </h3>
            <p>{task.day}</p>

        </div>
  )
}

export default Task
