import Task from './Task'

const Tasks = ({tasks, onDelete, onToggle}) => {
    return (
        <>
            {/* task = each ele (JSON) in tasks */}
            {tasks.map((task) => (
                //key is passed bc list generated by .map() needs a unique key
                //this key is not captured/used by the Task function 
                <Task key={task.id} task={task} onDelete={onDelete} onToggle = {onToggle}/>
                //onDelete is the name prop we decided to send 
                //{onDelete} is the function we received as an argument and is
                //being passed to Task again 

            ))}
        </>
    )
}

export default Tasks
