//TODO:
//NO DUPLICATED COLORS!

import { useState, createContext, useContext } from 'react'
import Select from 'react-select'

const AppContext = createContext(null);


const Lobby = () => {
    const [colors, setColors] = useState([
        { value: 'red', label: 'red', disabled: false },
        { value: 'blue', label: 'blue', disabled: false },
        { value: 'green', label: 'green', disabled: false },
        { value: 'purple', label: 'purple', disabled: false }
    ])

    const [players, setPlayers] = useState([
        {
            id: 1,
            color: 'white'
        },
        {
            id: 2,
            color: 'white'
        },
        {
            id: 3,
            color: 'white'
        },
        {
            id: 4,
            color: 'white'
        }
    ])

    return (
        <div>
            <Players players={players} />
        </div>
    )
}


const Players = ({ players }) => {
    return (
        <>
            {players.map((player) => (
                <Player key={player.id} player={player} />

            ))}
        </>
    )
}

const Player = ({ player }) => { //onDelete is a function passed up (to the parent file)
    //onDelete = passed up (executed in parent func) while task = passed down (executed here)


    return (
        <div className='container' style={{ backgroundColor: player.color }}>
            <Select
                options={colors}
                placeholder={'Player ' + player.id + ' Color'}
                onChange={(e) => {
                    console.log(player.id, e)
                    setPlayers(
                        players.map((play) =>
                            play.id === player.id ? { ...play, color: e.value }
                                : play
                        ))
                    if (player.color != "white") {
                        setColors(
                            colors.map((color) => 
                                color.value === player.color ? { ...color, disabled: false}
                                    : color
                            )
                        )
                        console.log(player.color)
                    }
                    e.disabled = true;
                }}
                isOptionDisabled={(option) => option.disabled}
            />
        </div>
    )
}


export default Lobby
