//was done using rface (from exetension)
import { useState } from "react"
import Header from './components/Header'
import Tasks from './components/Tasks'
import AddTask from './components/AddTask'

const App = () => {
  const [showAddTask, setShowAddTask] = useState(false)
  const [tasks, setTasks] = useState([
    {
        // don't need "" for prop names in JSON for JS
        id: 1,
        text: 'Doctors Appointment',
        day: 'Feb 5th at 2:30pm',
        reminder: true,
    },
    {
        id: 2,
        text: 'Meeting at School',
        day: 'Feb 6th at 1:30pm',
        reminder: true,
    },
    {
        id: 3,
        text: 'Food Shopping',
        day: 'Feb 5th at 2:30pm',
        reminder: false,
    },
  ])

  const addTask = (task) => {
    const id = Math.floor(Math.random() * 10000) + 1
    const newTask = { id, ...task }
    setTasks([...tasks, newTask])
  }


  // Delete Task
  const deleteTask = (id) => {
    //*** HOW TO CHANGE tasks (useState) 
    //for each task(ele) in tasks, only leave ones whose id != the id we received
    setTasks(tasks.filter((task) => task.id !== id))
  }

  const toggleReminder = (id) => {
    setTasks(
      tasks.map((task) => 
        task.id === id ? { ...task, reminder: !task.reminder}
        : task
    ))
  }

  return (
    // can only have 1 parent elment!
    <div className='container'>
      {/* this calls the Header function & passes an object with title=__ */}
      {/* surround ALL PROPS by {} */}
      <Header 
        onAdd = {() => {
        setShowAddTask(!showAddTask)}}
        showAdd={showAddTask}
      /> 

      {showAddTask && <AddTask onAdd={addTask} />}


      {tasks.length > 0 ? (
        <Tasks tasks={tasks} onDelete={deleteTask} onToggle={toggleReminder}/> 
      ) : (
        'No Tasks To Show'
      )}
    </div> 
  );
}

// export to index.js
export default App;
