//was done using rface (from exetension)
import Header from './components/Header'
import Tasks from './components/Tasks'
import { useState } from "react"


const App = () => {
  const [tasks, setTasks] = useState([
    {
        // don't need "" for prop names in JSON for JS
        id: 1,
        text: 'Doctors Appointment',
        day: 'Feb 5th at 2:30pm',
        reminder: true,
    },
    {
        id: 2,
        text: 'Meeting at School',
        day: 'Feb 6th at 1:30pm',
        reminder: true,
    },
    {
        id: 3,
        text: 'Food Shopping',
        day: 'Feb 5th at 2:30pm',
        reminder: false,
    },
  ])

  // Delete Task
  const deleteTask = (id) => {
    //for each task(ele) in tasks, only leave ones whose id != the id we received
    setTasks(tasks.filter((task) => task.id !== id))
  }

  return (
    // can only have 1 parent elment!
    <div className='container'>
      {/* this calls the Header function & passes an object with title=__ */}
      {/* surround ALL PROPS by {} */}
      <Header /> 
      <Tasks tasks={tasks} onDelete={deleteTask} />
    </div> 
  );
}

// export to index.js
export default App;
