import HomePage from "./components/HomePage"

const App = () => {


  return (
    <BrowserRouter>
    <div className='container'>
      {/* this calls the Header function & passes an object with title=__ */}
      {/* surround ALL PROPS by {} */}


      <Routes>
        <Route path="/" element={HomePage}/>
        <Route path="/about" element={<About/>}/>
      </Routes>
      <Footer />
    </div> 
    </BrowserRouter>
  );
}

// export to index.js
export default App;
