//created using rafce (set-up)
import PropTypes from 'prop-types'

const Button = ({color, text, onAdd}) => {

    return (
        <button 
            style={{backgroundColor: color}} 
            className={'btn'}
            // execute the onClick function received as an argument
            // {} needed for function calls 
            // Observe that this tag is not a function, so onClick here is a keyword, 
            // not a prop
            onClick = {onAdd}
        >
            {text}
        </button>
    )
}

Button.defaultProps = {
    color: 'steelblue'
}

Button.propTypes = {
    color: PropTypes.string,
    text: PropTypes.string,
    onClick: PropTypes.func
}

export default Button
